    private void addButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addButtonActionPerformed
        // Ambil teks dari field input
    String namaMenu = menuTxt.getText().trim();
    String jenisMasakan = jenisTxt.getText().trim();
    String bahan = bahanTextArea.getText().trim();

    if (namaMenu.isEmpty() || jenisMasakan.isEmpty() || bahan.isEmpty()) {
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Semua field harus diisi!", 
            "Peringatan", 
            javax.swing.JOptionPane.WARNING_MESSAGE);
        return;
    }

    // Membuat format item yang akan ditambahkan atau diperbarui
    String item = String.format("Menu: %s | Jenis: %s | Bahan: %s", namaMenu, jenisMasakan, bahan);

    if (editingIndex == -1) {
        // Menambahkan item baru ke list1
        list1.add(item);
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Resep berhasil ditambahkan!", 
            "Sukses", 
            javax.swing.JOptionPane.INFORMATION_MESSAGE);
    } else {
        // Memperbarui item yang sudah ada di list1 berdasarkan editingIndex
        list1.replaceItem(item, editingIndex);
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Resep berhasil diperbarui!", 
            "Sukses", 
            javax.swing.JOptionPane.INFORMATION_MESSAGE);

        // Reset editingIndex setelah update
        editingIndex = -1;
    }

    // Reset fields setelah Add atau Update
    menuTxt.setText("");
    jenisTxt.setText("");
    bahanTextArea.setText("");
    }//GEN-LAST:event_addButtonActionPerformed

    private void deleteButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteButtonActionPerformed
        // Periksa apakah ada item yang dipilih
    int selectedIndex = list1.getSelectedIndex();
    if (selectedIndex == -1) {
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Pilih menu yang ingin Anda hapus!", 
            "Peringatan", 
            javax.swing.JOptionPane.WARNING_MESSAGE);
        return;
    }

    // Konfirmasi penghapusan
    int confirm = javax.swing.JOptionPane.showConfirmDialog(this, 
        "Apakah Anda yakin ingin menghapus menu ini?", 
        "Konfirmasi", 
        javax.swing.JOptionPane.YES_NO_OPTION);

    if (confirm == javax.swing.JOptionPane.YES_OPTION) {
        // Hapus item dari list
        list1.remove(selectedIndex);

        // Notifikasi sukses
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Menu yang Anda pilih berhasil dihapus!", 
            "Sukses", 
            javax.swing.JOptionPane.INFORMATION_MESSAGE);
    }
    }                                            

