    private void simpanButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simpanButtonActionPerformed
        // Mendapatkan semua item dari list
    String[] items = list1.getItems();
    
    // Memeriksa jika ada item di dalam list
    if (items.length == 0) {
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Tidak ada resep yang disimpan!", 
            "Peringatan", 
            javax.swing.JOptionPane.WARNING_MESSAGE);
        return;
    }

    // Tentukan nama file untuk menyimpan di penyimpanan internal
    String fileName = "resep_masakan.txt"; // Nama file untuk menyimpan

    try {
        // Mendapatkan path direktori aplikasi atau folder khusus
        String path = System.getProperty("user.dir") + "/" + fileName; // Menyimpan di direktori aplikasi
        BufferedWriter writer = new BufferedWriter(new FileWriter(path));

        // Menyimpan setiap item dalam list ke file
        for (String item : items) {
            writer.write(item);
            writer.newLine();  // Menambahkan baris baru setelah setiap item
        }
        writer.close();  // Menutup BufferedWriter setelah selesai menulis

        // Menampilkan pesan berhasil
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Resep berhasil disimpan!", 
            "Sukses", 
            javax.swing.JOptionPane.INFORMATION_MESSAGE);
    } catch (IOException e) {
        // Menampilkan pesan error jika terjadi masalah saat menulis file
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Terjadi kesalahan saat menyimpan resep!", 
            "Error", 
            javax.swing.JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_simpanButtonActionPerformed

