    private void buttonEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonEditActionPerformed
        // Periksa apakah ada item yang dipilih
    int selectedIndex = list1.getSelectedIndex(); // Mendapatkan indeks item yang dipilih
    if (selectedIndex == -1) {
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Pilih item yang ingin diedit!", 
            "Peringatan", 
            javax.swing.JOptionPane.WARNING_MESSAGE);
        return;
    }

    // Mengambil item yang dipilih
    String selectedItem = list1.getItem(selectedIndex);

    // Memecah data berdasarkan tanda "|" untuk mendapatkan bagian menu, jenis, dan bahan
    String[] parts = selectedItem.split("\\|");
    if (parts.length == 3) {
        // Menampilkan data ke dalam text fields
        menuTxt.setText(parts[0].replace("Menu: ", "").trim());
        jenisTxt.setText(parts[1].replace("Jenis: ", "").trim());
        bahanTextArea.setText(parts[2].replace("Bahan: ", "").trim());
    }

    // Menyimpan indeks yang sedang diedit
    editingIndex = selectedIndex;
    }//GEN-LAST:event_buttonEditActionPerformed

